<script setup lang="ts">
import BaseChart from "@/components/HeFei/BaseChart.vue";
import type * as echarts from "echarts"
const usedSrc =
  'data:image/png;base64,'
const residueSrc = 'data:image/png;base64,'
const usedImg = new Image();
usedImg.src = usedSrc;
const residueImg = new Image();
residueImg.src = residueSrc;

const props = defineProps<{
  data: { used: number, residue: number }
}>()

const option = {
  tooltip: {
    trigger: 'item',
    formatter: '{a}<br/>{b}:{c}',
  },
  legend: {
    show: false,
  },
  series: [
    {
      name: '磁盘容量',
      type: 'pie',
      radius: ['80%', '95%'],
      emphasis: {
        scale: false,
      },
      label: {
        show: false,
      },
      labelLine: {
        show: false
      },
      data: []
    }
  ]
}
function setData(chart: echarts.ECharts, data: any) {
  const seriesData = [
    { 
      name: '已用', 
      value: data.used, 
      itemStyle: {
        color: {
          image: usedImg,
          repeat: 'repeat'
        },
      },
    },
    { 
      name: '剩余', 
      value: data.residue,
      itemStyle: {
        color: {
          image: residueImg,
          repeat: 'repeat'
        },
      },
    },
  ]
  chart.setOption({
    series: [
      {
        data: seriesData
      },
    ]
  })
}
</script>

<template>
  <div class="disk-chart">
    <div class="cycle-chart">
      <BaseChart :data="data" :options="option" @data-change="setData"/>
      <div class="total-info">
        <div class="total-gb">1000GB</div>
        <div class="total-label">总容量</div>
      </div>
    </div>
    <div class="disk-info">
      <div class="server-title">
        <span>{{ '综合气象大数据应用服务机器' }}</span>
      </div>
      <div class="server-ip">
        <span>IP：{{ '192.168.1.1' }}</span>
      </div>
      <div class="server-legend">
        <div class="legend-item">
          <div class="used-icon"></div>
          <span class="legend-type">已用:</span>
          <span>22G</span>
          <span class="legend-rate">22%</span>
        </div>
        <div class="legend-item">
          <div class="residue-icon"></div>
          <span class="legend-type">剩余:</span>
          <span>78G</span>
          <span class="legend-rate">78%</span>
        </div>
      </div>
    </div>
  </div>
</template>

